mapper.insert:
INSERT INTO RESERVATION (ReservationCode
						,ReservationCheckIn
						,ReservationCheckOut
						,ReservationPeopleNumber
						,ReservationDate
						,ReservationMemo
						,UserId
						,RoomCode
						,ReservationDays
						,Breakfast
						,TotalPrice
						,Mileage
)

VALUES ((select nvl(max(reservationcode)+1,0) from reservation)
		, #{ReservationCheckIn}
		, #{ReservationCheckOut}
		, #{ReservationPeopleNumber}
		, sysdate
		, #{ReservationMemo}
		, #{UserId}
		, #{RoomCode}
		, #{ReservationDays}
		, #{Breakfast}
		, #{TotalPrice}
		, #{Mileage}
)
