mapper.insert:
INSERT INTO RESERVATION (ReservationCode
						,ReservationCheckIn
						,ReservationCheckOut
						,ReservationPeopleNumber
						,ReservationDate
						,ReservationMemo
						,UserId
						,RoomCode
						,ReservationDays
						,Breakfast
						,TotalPrice
						,Mileage
						,Card
						,CardNum
						,CardValid
						,RoomType
)

VALUES ((select nvl(max(reservationcode)+1,0) from reservation)
		, #{ReservationCheckIn}
		, #{ReservationCheckOut}
		, #{ReservationPeopleNumber}
		, sysdate
		, #{ReservationMemo}
		, #{UserId}
		, #{RoomCode}
		, #{ReservationDays}
		, #{Breakfast}
		, #{TotalPrice}
		, #{Mileage}
		, #{Card}
		, #{CardNum}
		, #{CardValid}
		, #{RoomType}
)

mapper.select:

select *
	from (
		select rownum as rn, A.*
		from (
	SELECT *
	FROM Reservation
	order by ReservationCode desc

		) A
	) 
	where rn between ${start} and ${end}
